import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface Event {
  id: number;
  time: number;
  text: string;
  player: {
    name: string;
    shortName: string;
  };
  teamId: number;
  type: string;
  homeScore?: number;
  awayScore?: number;
}

interface MatchEventsProps {
  matchId: number;
  homeTeamId: number;
  awayTeamId: number;
}

const MatchEvents = ({ matchId, homeTeamId, awayTeamId }: MatchEventsProps) => {
  const [events, setEvents] = useState<Event[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get(`/event/${matchId}/events`);
        setEvents(response.data.events || []);
      } catch (error) {
        console.error('Ma√ß olaylarƒ± y√ºklenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, [matchId]);

  if (loading) return <div className="text-center">Y√ºkleniyor...</div>;

  const getEventIcon = (type: string) => {
    switch (type) {
      case 'goal':
        return '‚öΩ';
      case 'card':
        return 'üü®';
      case 'redCard':
        return 'üü•';
      case 'substitution':
        return 'üîÑ';
      default:
        return '‚Ä¢';
    }
  };

  const getEventClass = (teamId: number) => {
    return teamId === homeTeamId ? 'text-left' : 'text-right';
  };

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold mb-4">Ma√ß Olaylarƒ±</h3>
      <div className="space-y-2">
        {events.map((event) => (
          <div 
            key={event.id} 
            className={`flex items-center gap-2 ${
              event.teamId === homeTeamId ? 'justify-start' : 'justify-end'
            }`}
          >
            {event.teamId === homeTeamId ? (
              <>
                <span className="text-lg">{getEventIcon(event.type)}</span>
                <div>
                  <span className="font-medium">{event.player.name}</span>
                  <span className="text-sm text-gray-500 ml-2">{event.time}'</span>
                  {event.homeScore !== undefined && (
                    <span className="text-sm text-gray-600 ml-2">
                      {event.homeScore}-{event.awayScore}
                    </span>
                  )}
                </div>
              </>
            ) : (
              <>
                <div>
                  <span className="font-medium">{event.player.name}</span>
                  <span className="text-sm text-gray-500 mr-2">{event.time}'</span>
                  {event.homeScore !== undefined && (
                    <span className="text-sm text-gray-600 mr-2">
                      {event.homeScore}-{event.awayScore}
                    </span>
                  )}
                </div>
                <span className="text-lg">{getEventIcon(event.type)}</span>
              </>
            )}
          </div>
        ))}

        {events.length === 0 && (
          <p className="text-center text-gray-500">
            Hen√ºz ma√ß olayƒ± bulunmuyor.
          </p>
        )}
      </div>
    </div>
  );
};

export default MatchEvents;
