import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface MatchStatistics {
  statistics: {
    period: string;
    groups: {
      groupName: string;
      statisticsItems: {
        name: string;
        home: string;
        away: string;
      }[];
    }[];
  }[];
}

interface MatchEventsProps {
  matchId: number;
  homeTeamId: number;
  awayTeamId: number;
}

const MatchEvents = ({ matchId, homeTeamId, awayTeamId }: MatchEventsProps) => {
  const [stats, setStats] = useState<MatchStatistics | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await api.get(`/event/${matchId}/statistics`);
        console.log('API Response:', response.data);
        setStats(response.data);
      } catch (error) {
        console.error('MaÃ§ istatistikleri yÃ¼klenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, [matchId]);

  if (loading) return <div className="text-center">YÃ¼kleniyor...</div>;
  if (!stats) return null;

  const getEventIcon = (name: string) => {
    switch (name) {
      case 'Yellow cards':
        return 'ðŸŸ¨';
      case 'Red cards':
        return 'ðŸŸ¥';
      case 'Goals':
        return 'âš½';
      default:
        return null;
    }
  };

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold mb-4">MaÃ§ OlaylarÄ±</h3>
      <div className="flex flex-col space-y-4">
        {stats.statistics[0].groups.map((group, groupIndex) => (
          <div key={groupIndex}>
            <h4 className="font-medium text-gray-700 mb-2">{group.groupName}</h4>
            <div className="space-y-2">
              {group.statisticsItems.map((item, itemIndex) => {
                const icon = getEventIcon(item.name);
                if (!icon) return null;

                return (
                  <div key={itemIndex} className="flex justify-between items-center p-2 bg-gray-50 rounded-lg">
                    <div className="flex items-center gap-2">
                      <span className="text-lg">{icon}</span>
                      <span>{item.home}</span>
                    </div>
                    <span className="text-sm font-medium text-gray-600">{item.name}</span>
                    <div className="flex items-center gap-2">
                      <span>{item.away}</span>
                      <span className="text-lg">{icon}</span>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        ))}

        {stats.statistics[0].groups.length === 0 && (
          <p className="text-center text-gray-500">
            HenÃ¼z maÃ§ istatistikleri bulunmuyor.
          </p>
        )}
      </div>
    </div>
  );
};

export default MatchEvents;
