import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface Player {
  name: string;
  shortName: string;
  id: number;
}

interface MatchEvent {
  id: number;
  time: number;
  text: string;
  player?: Player;
  playerIn?: Player;
  playerOut?: Player;
  teamId?: number;
  incidentType: string;
  isHome: boolean;
  reason?: string;
  goalScore?: {
    home: number;
    away: number;
  };
  assist1?: Player;
  incidentClass?: string;
  injury?: boolean;
  homeScore?: number;
  awayScore?: number;
  confirmed?: boolean;
  addedTime?: number;
}

interface MatchEventsProps {
  matchId: number;
  homeTeamId: number;
  awayTeamId: number;
}

const formatMatchTime = (time: number, addedTime?: number) => {
  if (addedTime === 999) {
    return time === 45 ? 'HT' : 'FT';
  }
  return `${time}'${addedTime ? `+${addedTime}` : ''}`;
};

const MatchEvents = ({ matchId, homeTeamId, awayTeamId }: MatchEventsProps) => {
  const [events, setEvents] = useState<MatchEvent[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get(`/event/${matchId}/incidents`);
        console.log('API Response:', response.data);
        const filteredEvents = response.data.incidents.filter((event: MatchEvent) => 
          ['goal', 'card', 'substitution', 'period', 'varDecision'].includes(event.incidentType)
        );
        
        // MaÃ§ baÅŸlangÄ±cÄ± olayÄ±nÄ± ekle
        const kickoffEvent: MatchEvent = {
          id: -1,
          time: 0,
          text: 'KO',
          incidentType: 'period',
          isHome: false,
          homeScore: 0,
          awayScore: 0,
          teamId: undefined
        };
        
        // OlaylarÄ± ters Ã§evir ve maÃ§ baÅŸlangÄ±cÄ±nÄ± en baÅŸa ekle
        const reversedEvents = filteredEvents.reverse();
        setEvents([kickoffEvent, ...reversedEvents]);
      } catch (error) {
        console.error('MaÃ§ olaylarÄ± yÃ¼klenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, [matchId]);

  const getEventIcon = (type: string, incidentClass?: string) => {
    switch (type) {
      case 'goal':
        return 'âš½';
      case 'card':
        return incidentClass === 'yellow' ? 'ðŸŸ¨' : 
               incidentClass === 'red' ? 'ðŸŸ¥' : 'â¬œ';
      case 'substitution':
        return 'ðŸ”„';
      case 'varDecision':
        return 'ðŸ“º';
      default:
        return 'â€¢';
    }
  };

  const renderEvent = (event: MatchEvent) => {
    const isHome = event.isHome;
    const baseClasses = "flex items-center gap-1 text-sm";
    const alignmentClasses = isHome ? "justify-start" : "justify-end";

    const getEventBackground = (type: string, incidentClass?: string) => {
      switch (type) {
        case 'goal':
          return 'bg-green-900/30';
        case 'card':
          return incidentClass === 'yellow' ? 'bg-yellow-900/30' :
                 incidentClass === 'red' ? 'bg-red-900/30' : '';
        case 'varDecision':
          return 'bg-blue-900/30';
        default:
          return '';
      }
    };

    switch (event.incidentType) {
      case 'period':
        if (event.text === 'KO') {  // Kick-off (MaÃ§ baÅŸlangÄ±cÄ±)
          return (
            <div className="w-full flex justify-between items-center py-2 px-4 bg-gray-800/50">
              <div className="text-xs font-medium text-gray-400">
                MaÃ§ BaÅŸladÄ±
              </div>
              <div className="text-sm font-bold text-white">
                0 - 0
              </div>
            </div>
          );
        }
        return (
          <div className="w-full flex justify-between items-center py-2 px-4 bg-gray-800/50">
            <div className="text-xs font-medium text-gray-400">
              {event.text === 'HT' ? 'Ä°lk YarÄ± Sonu' : 'MaÃ§ Sonu'}
            </div>
            <div className="text-sm font-bold text-white">
              {event.homeScore} - {event.awayScore}
            </div>
          </div>
        );
      case 'goal':
        return (
          <div className={`${baseClasses} ${alignmentClasses} p-1.5 rounded ${getEventBackground('goal')}`}>
            {!isHome && getEventIcon(event.incidentType)}
            <div className={`flex flex-col ${isHome ? 'items-start' : 'items-end'}`}>
              <span className="font-medium text-white">{event.player?.name}</span>
              {event.assist1 && (
                <span className="text-xs text-emerald-400">Asist: {event.assist1.name}</span>
              )}
              <span className="text-xs text-emerald-300">
                {event.goalScore?.home} - {event.goalScore?.away}
              </span>
            </div>
            {isHome && getEventIcon(event.incidentType)}
          </div>
        );
      case 'card':
        return (
          <div className={`${baseClasses} ${alignmentClasses} p-1.5 rounded ${getEventBackground('card', event.incidentClass)}`}>
            {!isHome && getEventIcon(event.incidentType, event.incidentClass)}
            <div className={`flex flex-col ${isHome ? 'items-start' : 'items-end'}`}>
              <span className="text-white">{event.player?.name}</span>
              {event.reason && (
                <span className={`text-xs ${
                  event.incidentClass === 'yellow' ? 'text-yellow-300' :
                  event.incidentClass === 'red' ? 'text-red-300' : 'text-gray-500'
                }`}>
                  {event.reason}
                </span>
              )}
            </div>
            {isHome && getEventIcon(event.incidentType, event.incidentClass)}
          </div>
        );
      case 'substitution':
        return (
          <div className={`${baseClasses} ${alignmentClasses} p-1.5`}>
            {!isHome && getEventIcon(event.incidentType)}
            <div className={`flex flex-col ${isHome ? 'items-start' : 'items-end'} text-xs`}>
              <span className="text-emerald-500 font-medium">â†‘ {event.playerIn?.name}</span>
              <span className="text-rose-500 font-medium">â†“ {event.playerOut?.name}</span>
            </div>
            {isHome && getEventIcon(event.incidentType)}
          </div>
        );
      case 'varDecision':
        return (
          <div className={`${baseClasses} ${alignmentClasses} p-1.5 rounded ${getEventBackground('varDecision')}`}>
            {!isHome && getEventIcon(event.incidentType)}
            <div className={`flex flex-col ${isHome ? 'items-start' : 'items-end'}`}>
              <span className="text-white">{event.player?.name}</span>
              <span className="text-xs text-blue-300">
                {event.incidentClass === 'goalCancelled' ? 'Gol iptal edildi' : 'Gol onaylandÄ±'}
              </span>
            </div>
            {isHome && getEventIcon(event.incidentType)}
          </div>
        );
      default:
        return null;
    }
  };

  if (loading) return <div className="text-center text-xs text-gray-400">YÃ¼kleniyor...</div>;

  return (
    <div className="flex justify-center">
      <div className="bg-gray-900 rounded-lg text-sm w-full max-w-lg">
        <div className="divide-y divide-gray-800">
          {events.map((event) => (
            <div key={event.id} className="flex items-center px-3 py-1.5 hover:bg-gray-800/50">
              <div className="w-12 text-xs text-gray-500 shrink-0 text-center">
                {formatMatchTime(event.time, event.addedTime)}
              </div>
              <div className="flex-1 px-4">
                {renderEvent(event)}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MatchEvents;
