import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import TeamLogo from '../components/TeamLogo';
import DateSelector from '../components/DateSelector';
import MatchCard from '../components/MatchCard';

interface Match {
  id: string;
  homeTeam: {
    id: string;
    name: string;
    shortName: string;
  };
  awayTeam: {
    id: string;
    name: string;
    shortName: string;
  };
  tournament: {
    name: string;
    category: {
      name: string;
      flag?: string;
    };
    uniqueTournament: {
      name: string;
      id: number;
    };
  };
  status: {
    type: string;
    description: string;
  };
  startTimestamp: number;
  homeScore?: {
    current: number;
  };
  awayScore?: {
    current: number;
  };
}

const FAVORITE_LEAGUES = [
  'LaLiga',
  'Premier League',
  'Süper Lig',
  'Serie A',
  'Bundesliga',
  'Ligue 1'
];

// Öncelikli ligler - Türkiye en başta
const PRIORITY_LEAGUES = [
  'Turkey - Süper Lig',
  'Europe - UEFA Champions League',
  'Europe - UEFA Europa League',
  'Europe - UEFA Europa Conference League',
  'England - Premier League',
  'Italy - Serie A',
  'Spain - LaLiga',
  'Germany - Bundesliga',
  'France - Ligue 1',
  'Netherlands - Eredivisie',
  'Portugal - Primeira Liga',
  'Belgium - Pro League'
];

// Lig öncelik sıralaması - Türkiye en başta
const LEAGUE_PRIORITY: { [key: string]: number } = {
  'Süper Lig': 1,
  'UEFA Champions League': 2,
  'UEFA Europa League': 3,
  'UEFA Europa Conference League': 4,
  'Premier League': 5,
  'Serie A': 6,
  'LaLiga': 7,
  'Bundesliga': 8,
  'Ligue 1': 9,
  'Eredivisie': 10,
  'Primeira Liga': 11,
  'Pro League': 12
};

const DailyMatches: React.FC = () => {
  const [matches, setMatches] = useState<Match[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [favoriteMatches, setFavoriteMatches] = useState<string[]>(() => {
    const saved = localStorage.getItem('favoriteMatches');
    return saved ? JSON.parse(saved) : [];
  });

  useEffect(() => {
    fetchMatches();
  }, [selectedDate]);

  const fetchMatches = async () => {
    try {
      setLoading(true);
      const formattedDate = selectedDate.toISOString().split('T')[0];
      const response = await fetch(`https://www.sofascore.com/api/v1/sport/football/scheduled-events/${formattedDate}`);
      const data = await response.json();
      
      // Maçları tarihe göre sırala
      const sortedMatches = data.events.sort((a: Match, b: Match) => 
        a.startTimestamp - b.startTimestamp
      );

      setMatches(sortedMatches);
    } catch (error) {
      console.error('Maçlar yüklenirken hata:', error);
    } finally {
      setLoading(false);
    }
  };

  const toggleFavorite = (matchId: string) => {
    setFavoriteMatches(prev => {
      const newFavorites = prev.includes(matchId)
        ? prev.filter(id => id !== matchId)
        : [...prev, matchId];
      
      localStorage.setItem('favoriteMatches', JSON.stringify(newFavorites));
      return newFavorites;
    });
  };

  // Maçları liglere göre grupla ve sırala
  const groupAndSortMatches = (matches: Match[]) => {
    const groups = matches.reduce((groups: {[key: string]: Match[]}, match) => {
      const key = `${match.tournament.category.name} - ${match.tournament.uniqueTournament.name}`;
      if (!groups[key]) {
        groups[key] = [];
      }
      groups[key].push(match);
      return groups;
    }, {});

    // Öncelikli ligleri başa al ve kendi içinde sırala
    return Object.entries(groups).sort(([a], [b]) => {
      const isAPriority = PRIORITY_LEAGUES.includes(a);
      const isBPriority = PRIORITY_LEAGUES.includes(b);

      // Türkiye ligi her zaman en başta
      if (a.includes('Turkey - Süper Lig')) return -1;
      if (b.includes('Turkey - Süper Lig')) return 1;

      // Avrupa kupaları Türkiye'den sonra
      if (a.includes('UEFA') && !b.includes('UEFA')) return -1;
      if (!a.includes('UEFA') && b.includes('UEFA')) return 1;

      // Diğer öncelikli ligler
      if (isAPriority && !isBPriority) return -1;
      if (!isAPriority && isBPriority) return 1;

      // Öncelikli ligler arasında kendi sıralaması
      if (isAPriority && isBPriority) {
        return PRIORITY_LEAGUES.indexOf(a) - PRIORITY_LEAGUES.indexOf(b);
      }

      // Diğer ligler alfabetik
      return a.localeCompare(b);
    });
  };

  const sortedLeagues = groupAndSortMatches(matches);

  return (
    <div className="min-h-screen bg-gray-900">
      <div className="max-w-6xl mx-auto p-4">
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-2xl font-bold text-white">Günlük Maçlar</h1>
          <DateSelector 
            selectedDate={selectedDate}
            onChange={setSelectedDate}
          />
        </div>

        {loading ? (
          <div className="text-center text-gray-400 py-8">Yükleniyor...</div>
        ) : (
          <div className="space-y-6">
            {/* Favori Maçlar */}
            {favoriteMatches.length > 0 && (
              <div className="bg-gray-800/50 rounded-lg overflow-hidden">
                <div className="bg-gray-800 px-4 py-2">
                  <h2 className="text-lg font-semibold text-white">Favori Maçlar</h2>
                </div>
                <div className="divide-y divide-gray-700">
                  {matches
                    .filter(match => favoriteMatches.includes(match.id))
                    .map(match => (
                      <MatchCard
                        key={match.id}
                        match={match}
                        onClick={() => {}}
                        onToggleFavorite={toggleFavorite}
                        isFavorite={true}
                      />
                    ))}
                </div>
              </div>
            )}

            {/* Tüm Ligler */}
            {sortedLeagues.map(([leagueName, leagueMatches]) => (
              <div key={leagueName} className="bg-gray-800/50 rounded-lg overflow-hidden">
                <div className="bg-gray-800 px-4 py-2 flex items-center justify-between">
                  <h2 className="text-lg font-semibold text-white">{leagueName}</h2>
                  {PRIORITY_LEAGUES.includes(leagueName) && (
                    <span className="text-xs bg-yellow-500 text-black px-2 py-1 rounded-full">
                      Öncelikli Lig
                    </span>
                  )}
                </div>
                <div className="divide-y divide-gray-700">
                  {leagueMatches.map(match => (
                    <MatchCard
                      key={match.id}
                      match={match}
                      onClick={() => {}}
                      onToggleFavorite={toggleFavorite}
                      isFavorite={favoriteMatches.includes(match.id)}
                    />
                  ))}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default DailyMatches;
