import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface MatchEvent {
  id: number;
  time: number;
  text: string;
  player?: {
    name: string;
    shortName: string;
  };
  teamId: number;
  incidentType: string;
  isHome: boolean;
}

interface MatchEventsProps {
  matchId: number;
  homeTeamId: number;
  awayTeamId: number;
}

const MatchEvents = ({ matchId, homeTeamId, awayTeamId }: MatchEventsProps) => {
  const [events, setEvents] = useState<MatchEvent[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get(`/event/${matchId}/incidents`);
        console.log('API Response:', response.data);
        const filteredEvents = response.data.incidents.filter((event: MatchEvent) => 
          ['goal', 'card', 'yellowCard', 'redCard', 'yellowRed', 'substitution'].includes(event.incidentType)
        );
        setEvents(filteredEvents);
      } catch (error) {
        console.error('MaÃ§ olaylarÄ± yÃ¼klenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, [matchId]);

  const getEventIcon = (type: string) => {
    switch (type) {
      case 'goal':
        return 'âš½';
      case 'yellowCard':
        return 'ðŸŸ¨';
      case 'redCard':
        return 'ðŸŸ¥';
      case 'yellowRed':
        return 'ðŸŸ¨ðŸŸ¥';
      case 'substitution':
        return 'ðŸ”„';
      default:
        return 'â€¢';
    }
  };

  const getEventDescription = (event: MatchEvent) => {
    if (!event.player?.name) return event.text;

    switch (event.incidentType) {
      case 'goal':
        return `${event.player.name} (Gol)`;
      case 'yellowCard':
        return `${event.player.name} (SarÄ± Kart)`;
      case 'redCard':
        return `${event.player.name} (KÄ±rmÄ±zÄ± Kart)`;
      case 'yellowRed':
        return `${event.player.name} (Ä°kinci SarÄ± Karttan KÄ±rmÄ±zÄ±)`;
      case 'substitution':
        return event.text;
      default:
        return event.text;
    }
  };

  if (loading) return <div className="text-center">YÃ¼kleniyor...</div>;

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold mb-4">MaÃ§ OlaylarÄ±</h3>
      <div className="flex flex-col space-y-2">
        {events.map((event) => (
          <div 
            key={event.id} 
            className={`flex items-center gap-2 p-2 rounded-lg ${
              event.isHome ? 'justify-start' : 'justify-end'
            } ${
              event.incidentType === 'goal' ? 'bg-green-50' :
              event.incidentType.includes('card') ? 'bg-red-50' :
              'bg-gray-50'
            }`}
          >
            {event.isHome ? (
              <>
                <span className="text-lg">{getEventIcon(event.incidentType)}</span>
                <div>
                  <span className="font-medium">{getEventDescription(event)}</span>
                  <span className="text-sm text-gray-500 ml-2">{event.time}'</span>
                </div>
              </>
            ) : (
              <>
                <div>
                  <span className="font-medium">{getEventDescription(event)}</span>
                  <span className="text-sm text-gray-500 mr-2">{event.time}'</span>
                </div>
                <span className="text-lg">{getEventIcon(event.incidentType)}</span>
              </>
            )}
          </div>
        ))}

        {events.length === 0 && (
          <p className="text-center text-gray-500">
            HenÃ¼z maÃ§ olayÄ± bulunmuyor.
          </p>
        )}
      </div>
    </div>
  );
};

export default MatchEvents;
