import axios, { AxiosRequestConfig } from 'axios';
import { auth } from '../firebase';

const api = axios.create({
  baseURL: 'http://localhost:5000/api/v4',  // Proxy sunucusu URL'si
});

// Her istekte Firebase token'ını ekle
api.interceptors.request.use(async (config: AxiosRequestConfig) => {
  const user = auth.currentUser;
  if (user) {
    const token = await user.getIdToken();
    if (config.headers) {
      config.headers.Authorization = `Bearer ${token}`;
    }
  }
  // Football-data.org API anahtarını ekleyin
  if (config.headers) {
    config.headers['X-Auth-Token'] = '239b33d39ec84db68c5938e79b16eb8f'; // API anahtarınızı buraya ekleyin
  }
  return config;
});

export { api };
