import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import TeamLogo from '../components/TeamLogo';

interface Match {
  id: string;
  homeTeam: {
    id: string;
    name: string;
  };
  awayTeam: {
    id: string;
    name: string;
  };
  tournament: {
    name: string;
    uniqueTournament: {
      name: string;
      id: number;
    };
  };
  status: {
    type: string;
    description: string;
  };
  startTimestamp: number;
  homeScore?: {
    current: number;
  };
  awayScore?: {
    current: number;
  };
}

const FAVORITE_LEAGUES = [
  'LaLiga',
  'Premier League',
  'Süper Lig',
  'Serie A',
  'Bundesliga',
  'Ligue 1'
];

const DailyMatches: React.FC = () => {
  const [matches, setMatches] = useState<Match[]>([]);
  const [loading, setLoading] = useState(true);
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);

  useEffect(() => {
    fetchMatches();
  }, [date]);

  const fetchMatches = async () => {
    try {
      setLoading(true);
      const formattedDate = date.replace(/-/g, '-');
      const response = await fetch(`https://www.sofascore.com/api/v1/sport/football/scheduled-events/${formattedDate}`);
      const data = await response.json();
      
      // Maçları tarihe göre sırala
      const sortedMatches = data.events.sort((a: Match, b: Match) => 
        a.startTimestamp - b.startTimestamp
      );

      setMatches(sortedMatches);
    } catch (error) {
      console.error('Maçlar yüklenirken hata:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (timestamp: number) => {
    return new Date(timestamp * 1000).toLocaleTimeString('tr-TR', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Maçları liglere göre grupla
  const groupedMatches = matches.reduce((groups: {[key: string]: Match[]}, match) => {
    const tournamentName = match.tournament.uniqueTournament.name;
    if (!groups[tournamentName]) {
      groups[tournamentName] = [];
    }
    groups[tournamentName].push(match);
    return groups;
  }, {});

  // Ligleri favori ve diğerleri olarak grupla
  const sortedTournaments = Object.entries(groupedMatches).sort(([a], [b]) => {
    const aIsFavorite = FAVORITE_LEAGUES.includes(a);
    const bIsFavorite = FAVORITE_LEAGUES.includes(b);
    
    if (aIsFavorite && !bIsFavorite) return -1;
    if (!aIsFavorite && bIsFavorite) return 1;
    return a.localeCompare(b);
  });

  return (
    <div className="max-w-7xl mx-auto p-4">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-white">Günlük Maçlar</h1>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="bg-gray-800 text-white px-4 py-2 rounded border border-gray-700"
        />
      </div>

      <div className="space-y-8">
        {loading ? (
          <div className="text-center text-gray-400">Yükleniyor...</div>
        ) : sortedTournaments.length === 0 ? (
          <div className="text-center text-gray-400">Bu tarihte maç bulunamadı</div>
        ) : (
          sortedTournaments.map(([tournamentName, tournamentMatches]) => (
            <div key={tournamentName} className="bg-gray-800 rounded-lg p-4">
              <div className="flex items-center gap-2 mb-4">
                <h2 className="text-xl font-semibold text-white">{tournamentName}</h2>
                {FAVORITE_LEAGUES.includes(tournamentName) && (
                  <span className="bg-yellow-500 text-xs px-2 py-1 rounded-full">
                    Favori Lig
                  </span>
                )}
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {tournamentMatches.map((match) => (
                  <Link 
                    key={match.id}
                    to={`/match/${match.id}`}
                    className="bg-gray-700 p-4 rounded hover:bg-gray-600 transition flex flex-col"
                  >
                    <div className="flex justify-between items-center mb-4">
                      <span className="text-gray-400 text-sm">
                        {formatTime(match.startTimestamp)}
                      </span>
                      {match.status.type === 'inProgress' && (
                        <span className="bg-green-500 text-white text-xs px-2 py-1 rounded-full">
                          Canlı
                        </span>
                      )}
                    </div>

                    <div className="flex justify-between items-center mb-2">
                      <div className="flex items-center gap-3">
                        <TeamLogo teamId={match.homeTeam.id} className="w-8 h-8" />
                        <span className="text-white">{match.homeTeam.name}</span>
                      </div>
                      {match.status.type === 'inProgress' && (
                        <span className="text-white font-bold">
                          {match.homeScore?.current}
                        </span>
                      )}
                    </div>

                    <div className="flex justify-between items-center">
                      <div className="flex items-center gap-3">
                        <TeamLogo teamId={match.awayTeam.id} className="w-8 h-8" />
                        <span className="text-white">{match.awayTeam.name}</span>
                      </div>
                      {match.status.type === 'inProgress' && (
                        <span className="text-white font-bold">
                          {match.awayScore?.current}
                        </span>
                      )}
                    </div>
                  </Link>
                ))}
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default DailyMatches;
