import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import TeamLogo from '../components/TeamLogo';

interface Match {
  id: string;
  homeTeam: {
    id: string;
    name: string;
    shortName: string;
  };
  awayTeam: {
    id: string;
    name: string;
    shortName: string;
  };
  tournament: {
    name: string;
    category: {
      name: string;
    };
    uniqueTournament: {
      name: string;
      id: number;
    };
  };
  status: {
    type: string;
    description: string;
  };
  startTimestamp: number;
  homeScore?: {
    current: number;
  };
  awayScore?: {
    current: number;
  };
}

const FAVORITE_LEAGUES = [
  'LaLiga',
  'Premier League',
  'Süper Lig',
  'Serie A',
  'Bundesliga',
  'Ligue 1'
];

// Lig öncelik sıralaması
const LEAGUE_PRIORITY: { [key: string]: number } = {
  'Champions League': 1,
  'Europa League': 2,
  'Conference League': 3,
  'Süper Lig': 4,        // Türkiye
  'LaLiga': 5,           // İspanya
  'Premier League': 6,    // İngiltere
  'Serie A': 7,          // İtalya
  'Bundesliga': 8,       // Almanya
  'Ligue 1': 9,          // Fransa
  'Eredivisie': 10,      // Hollanda
  'Primeira Liga': 11,   // Portekiz
  'Pro League': 12,      // Belçika
  // Diğer ligler için ülke puanına göre devam eder
};

const DailyMatches: React.FC = () => {
  const [matches, setMatches] = useState<Match[]>([]);
  const [loading, setLoading] = useState(true);
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);

  useEffect(() => {
    fetchMatches();
  }, [date]);

  const fetchMatches = async () => {
    try {
      setLoading(true);
      const formattedDate = date.replace(/-/g, '-');
      const response = await fetch(`https://www.sofascore.com/api/v1/sport/football/scheduled-events/${formattedDate}`);
      const data = await response.json();
      
      // Maçları tarihe göre sırala
      const sortedMatches = data.events.sort((a: Match, b: Match) => 
        a.startTimestamp - b.startTimestamp
      );

      setMatches(sortedMatches);
    } catch (error) {
      console.error('Maçlar yüklenirken hata:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (timestamp: number) => {
    return new Date(timestamp * 1000).toLocaleTimeString('tr-TR', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Maçları kategorilere ayır
  const categorizeMatches = (matches: Match[]) => {
    const favoriteMatches = matches.filter(match => 
      ['Champions League', 'Europa League'].includes(match.tournament.uniqueTournament.name)
    );

    const otherMatches = matches.filter(match => 
      !['Champions League', 'Europa League'].includes(match.tournament.uniqueTournament.name)
    );

    return {
      favoriteMatches: groupMatchesByTournament(favoriteMatches),
      otherMatches: groupMatchesByTournament(otherMatches)
    };
  };

  const groupMatchesByTournament = (matches: Match[]) => {
    const groups = matches.reduce((groups: {[key: string]: Match[]}, match) => {
      // Lig adını ve ülkeyi birlikte kullan
      const key = `${match.tournament.category.name} - ${match.tournament.uniqueTournament.name}`;
      if (!groups[key]) {
        groups[key] = [];
      }
      groups[key].push(match);
      return groups;
    }, {});

    // Ligleri öncelik sırasına göre sırala
    return Object.entries(groups)
      .sort(([a], [b]) => {
        const tournamentA = a.split(' - ')[1];
        const tournamentB = b.split(' - ')[1];
        
        // Öncelik tablosunda varsa ona göre, yoksa ülke adına göre sırala
        const priorityA = LEAGUE_PRIORITY[tournamentA] || 1000;
        const priorityB = LEAGUE_PRIORITY[tournamentB] || 1000;
        
        if (priorityA === priorityB) {
          // Aynı önceliğe sahiplerse ülke adına göre sırala
          return a.localeCompare(b);
        }
        
        return priorityA - priorityB;
      })
      .reduce((obj: {[key: string]: Match[]}, [key, value]) => {
        obj[key] = value;
        return obj;
      }, {});
  };

  const renderMatch = (match: Match) => (
    <Link 
      key={match.id}
      to={`/match/${match.id}`}
      className="flex items-center justify-between py-3 px-4 hover:bg-gray-800 transition rounded"
    >
      <div className="flex items-center gap-2 w-[45%]">
        <TeamLogo teamId={match.homeTeam.id} className="w-6 h-6" />
        <span className="text-white text-sm">{match.homeTeam.shortName}</span>
      </div>

      <div className="flex flex-col items-center min-w-[60px]">
        <span className="text-gray-400 text-xs">
          {formatTime(match.startTimestamp)}
        </span>
        {match.status.type === 'finished' && (
          <div className="text-white text-sm">
            {match.homeScore?.current} - {match.awayScore?.current}
          </div>
        )}
        {match.status.type === 'inProgress' && (
          <div className="text-green-500 text-sm">
            {match.homeScore?.current} - {match.awayScore?.current}
          </div>
        )}
      </div>

      <div className="flex items-center justify-end gap-2 w-[45%]">
        <span className="text-white text-sm">{match.awayTeam.shortName}</span>
        <TeamLogo teamId={match.awayTeam.id} className="w-6 h-6" />
      </div>
    </Link>
  );

  const { favoriteMatches, otherMatches } = categorizeMatches(matches);

  return (
    <div className="max-w-5xl mx-auto p-4">
      {/* Favori Maçlar */}
      {Object.entries(favoriteMatches).map(([tournamentName, matches]) => (
        <div key={tournamentName} className="mb-6">
          <div className="bg-gray-800/50 rounded-lg overflow-hidden">
            <div className="flex items-center gap-2 px-4 py-2 bg-gray-800">
              <h2 className="text-white font-medium">{tournamentName}</h2>
            </div>
            <div className="divide-y divide-gray-700">
              {matches.map(renderMatch)}
            </div>
          </div>
        </div>
      ))}

      {/* Diğer Ligler */}
      <div className="text-gray-400 text-lg mb-4">Diğer Ligler</div>
      <div className="space-y-6">
        {Object.entries(otherMatches).map(([tournamentName, matches]) => (
          <div key={tournamentName} className="bg-gray-800/50 rounded-lg overflow-hidden">
            <div className="flex items-center gap-2 px-4 py-2 bg-gray-800">
              <h2 className="text-white font-medium">{tournamentName}</h2>
            </div>
            <div className="divide-y divide-gray-700">
              {matches.map(renderMatch)}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DailyMatches;
