import React, { useState, useEffect } from 'react';
import { api } from '../services/api';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

interface Match {
  id: number;
  homeTeam: { name: string; };
  awayTeam: { name: string; };
  startTime: string;
}

const DailyMatches = () => {
  const [matches, setMatches] = useState<Match[]>([]);
  const [selectedDate, setSelectedDate] = useState<Date>(new Date());
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMatches = async () => {
      setLoading(true);
      try {
        const formattedDate = selectedDate.toISOString().split('T')[0];
        const response = await api.get(`/sport/football/scheduled-events/${formattedDate}`);
        setMatches(response.data.events);
      } catch (error) {
        console.error('Maçlar yüklenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMatches();
  }, [selectedDate]);

  const handleDateChange = (date: Date | null) => {
    if (date) {
      setSelectedDate(date);
    }
  };

  return (
    <div className="container mx-auto p-4">
      <div className="flex items-center gap-4 mb-6">
        <h1 className="text-2xl font-bold">Günlük Maçlar</h1>
        <DatePicker
          selected={selectedDate}
          onChange={handleDateChange}
          dateFormat="dd/MM/yyyy"
          className="border p-2 rounded"
        />
      </div>

      {loading ? (
        <div className="text-center">Yükleniyor...</div>
      ) : (
        <div className="grid gap-4">
          {matches.map((match) => (
            <div key={match.id} className="bg-white rounded-lg shadow p-4">
              <div className="flex justify-between items-center">
                <div className="flex-1">
                  <p className="font-semibold">{match.homeTeam.name}</p>
                </div>
                
                <div className="px-4">
                  <span className="text-sm text-gray-500">
                    {new Date(match.startTime).toLocaleTimeString('tr-TR', {
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </span>
                </div>
                
                <div className="flex-1 text-right">
                  <p className="font-semibold">{match.awayTeam.name}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default DailyMatches;
