import React, { useState } from 'react';

interface TeamLogoProps {
  teamId: string;
  className?: string;
  size?: 'small' | 'medium' | 'large';
}

const TeamLogo: React.FC<TeamLogoProps> = ({ teamId, className = "w-12 h-12", size = 'medium' }) => {
  const [error, setError] = useState(false);

  const sizeClasses = {
    small: 'w-6 h-6',
    medium: 'w-8 h-8',
    large: 'w-24 h-24'
  };

  if (error || !teamId) {
    return (
      <div className={`${className} flex items-center justify-center bg-gray-700 rounded`}>
        <span className="text-white text-sm font-medium">?</span>
      </div>
    );
  }

  return (
    <div className={`${className} flex items-center justify-center`}>
      <img 
        src={`https://img.sofascore.com/api/v1/team/${teamId}/image`}
        alt="team logo"
        className={`${sizeClasses[size]} object-contain`}
        onError={() => setError(true)}
      />
    </div>
  );
};

export default TeamLogo;
