import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface Player {
  name: string;
  shortName: string;
  id: number;
}

interface MatchEvent {
  id: number;
  time: number;
  text: string;
  player?: Player;
  playerIn?: Player;
  playerOut?: Player;
  teamId: number;
  incidentType: string;
  isHome: boolean;
  reason?: string;
  goalScore?: {
    home: number;
    away: number;
  };
  assist1?: Player;
  incidentClass?: string;
  injury?: boolean;
  homeScore?: number;
  awayScore?: number;
  confirmed?: boolean;
  addedTime?: number;
}

interface MatchEventsProps {
  matchId: number;
  homeTeamId: number;
  awayTeamId: number;
}

const MatchEvents = ({ matchId, homeTeamId, awayTeamId }: MatchEventsProps) => {
  const [events, setEvents] = useState<MatchEvent[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get(`/event/${matchId}/incidents`);
        console.log('API Response:', response.data);
        const filteredEvents = response.data.incidents.filter((event: MatchEvent) => 
          ['goal', 'card', 'substitution', 'period', 'varDecision'].includes(event.incidentType)
        );
        setEvents(filteredEvents.reverse());
      } catch (error) {
        console.error('MaÃ§ olaylarÄ± yÃ¼klenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, [matchId]);

  const getEventIcon = (type: string, incidentClass?: string) => {
    switch (type) {
      case 'goal':
        return 'âš½';
      case 'card':
        return incidentClass === 'yellow' ? 'ðŸŸ¨' : 
               incidentClass === 'red' ? 'ðŸŸ¥' : 'â¬œ';
      case 'substitution':
        return 'ðŸ”„';
      case 'varDecision':
        return 'ðŸ“º';
      default:
        return 'â€¢';
    }
  };

  const renderEvent = (event: MatchEvent) => {
    switch (event.incidentType) {
      case 'period':
        return (
          <div className="w-full text-center py-2 text-gray-200 bg-gray-800 rounded">
            {event.text} {event.homeScore}-{event.awayScore}
          </div>
        );
      case 'goal':
        return (
          <div className="flex items-center gap-2 text-gray-200">
            <span className="font-bold">{event.player?.name}</span>
            {event.assist1 && (
              <span className="text-gray-400 text-sm">
                Assist: {event.assist1.name}
              </span>
            )}
            <span className="text-gray-400">
              {event.goalScore?.home}-{event.goalScore?.away}
            </span>
            {getEventIcon(event.incidentType)}
          </div>
        );
      case 'card':
        return (
          <div className="flex items-center gap-2 text-gray-200">
            <span>{event.player?.name}</span>
            <span className="text-gray-400 text-sm">{event.reason}</span>
            {getEventIcon(event.incidentType, event.incidentClass)}
          </div>
        );
      case 'substitution':
        return (
          <div className="flex items-center gap-2 text-gray-200">
            <div>
              <span className="text-green-500">In: {event.playerIn?.name}</span>
              <span className="mx-2 text-gray-400">Out: </span>
              <span className="text-red-500">{event.playerOut?.name}</span>
            </div>
            {getEventIcon(event.incidentType)}
          </div>
        );
      case 'varDecision':
        return (
          <div className="flex items-center gap-2 text-gray-200">
            <span>{event.player?.name}</span>
            <span className="text-gray-400">
              Goal {event.incidentClass === 'goalCancelled' ? 'cancelled' : 'awarded'}
            </span>
            {getEventIcon(event.incidentType)}
          </div>
        );
      default:
        return null;
    }
  };

  if (loading) return <div className="text-center text-gray-200">YÃ¼kleniyor...</div>;

  return (
    <div className="bg-gray-900 p-4 rounded-lg">
      <div className="space-y-2">
        {events.map((event) => (
          <div key={event.id} className="flex items-center gap-2">
            <div className="w-12 text-gray-400 text-sm">
              {event.time}'{event.addedTime && `+${event.addedTime}`}
            </div>
            {renderEvent(event)}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MatchEvents;
