import React from 'react';
import { format, addDays, subDays } from 'date-fns';
import { tr } from 'date-fns/locale';

interface DateSelectorProps {
  selectedDate: Date;
  onChange: (date: Date) => void;
}

const DateSelector: React.FC<DateSelectorProps> = ({ selectedDate, onChange }) => {
  const goToPreviousDay = () => {
    onChange(subDays(selectedDate, 1));
  };

  const goToNextDay = () => {
    onChange(addDays(selectedDate, 1));
  };

  return (
    <div className="flex items-center bg-gray-800 rounded-lg overflow-hidden shadow-lg">
      <button
        onClick={goToPreviousDay}
        className="px-4 py-2 text-gray-400 hover:text-white hover:bg-gray-700 transition-all"
      >
        ◀
      </button>

      <div className="px-6 py-2 text-white font-medium border-l border-r border-gray-700">
        <div className="text-sm text-gray-400">
          {format(selectedDate, 'EEEE', { locale: tr })}
        </div>
        <div className="text-lg">
          {format(selectedDate, 'd MMMM yyyy', { locale: tr })}
        </div>
      </div>

      <button
        onClick={goToNextDay}
        className="px-4 py-2 text-gray-400 hover:text-white hover:bg-gray-700 transition-all"
      >
        ▶
      </button>
    </div>
  );
};

export default DateSelector;
