import React, { useState, useEffect } from 'react';

interface TeamLogoProps {
  team: string;
  className?: string;
  fallback?: React.ReactNode;
}

const TeamLogo: React.FC<TeamLogoProps> = ({ team, className = "w-12 h-12", fallback }) => {
  const [error, setError] = useState(false);

  // Konsol hatalarını gizle
  useEffect(() => {
    const originalError = console.error;
    console.error = (...args: any[]) => {
      if (args[0]?.includes?.('team-logos')) return;
      originalError.apply(console, args);
    };
    return () => {
      console.error = originalError;
    };
  }, []);

  // Eğer hata varsa veya takım adı yoksa fallback'i göster
  if (error || !team) {
    return (
      <div className={`${className} flex items-center justify-center`}>
        {fallback || (
          <div className="w-12 h-12 bg-gray-700 rounded flex items-center justify-center text-white text-xs">
            {team?.substring(0, 2).toUpperCase() || 'NA'}
          </div>
        )}
      </div>
    );
  }

  // Firebase Storage URL'ini oluştur
  const baseUrl = process.env.NODE_ENV === 'development' 
    ? 'http://localhost:9199/v0/b/iddaa-app-8011f.appspot.com/o'
    : 'https://firebasestorage.googleapis.com/v0/b/iddaa-app-8011f.appspot.com/o';

  // Önce takımın kendi logosunu dene, hata verirse Space Soldiers logosunu kullan
  const teamLogoUrl = `${baseUrl}/team-logos%2F${team}?alt=media`;
  const defaultLogoUrl = `${baseUrl}/team-logos%2FSpace_Soldiers_Logo.jpg?alt=media`;

  return (
    <div className={`${className} flex items-center justify-center`}>
      <img 
        src={teamLogoUrl}
        alt={`${team} logo`} 
        className="w-full h-full object-contain"
        onError={(e) => {
          e.preventDefault(); // Hata olayını engelle
          const imgElement = e.target as HTMLImageElement;
          imgElement.src = defaultLogoUrl;
          imgElement.onerror = (e) => {
            e.preventDefault(); // Hata olayını engelle
            setError(true);
          };
        }}
      />
    </div>
  );
};

export default TeamLogo;
