import { initializeApp } from 'firebase/app';
import { getStorage } from 'firebase/storage';
import { getFirestore } from 'firebase/firestore';

// Firebase yapılandırması
const app = initializeApp({
  apiKey: "AIzaSyBQZNSMq49Tqx0tFHvYYTP0hzpZTX_qXfk",
  authDomain: "iddaa-app-8011f.firebaseapp.com",
  projectId: "iddaa-app-8011f",
  storageBucket: "iddaa-app-8011f.appspot.com",
  messagingSenderId: "517475659873",
  appId: "1:517475659873:web:d7d7e27c7c14eb91d2945d"
});

// Firebase servislerini başlat
const storage = getStorage(app);
const db = getFirestore(app);

export { storage, db };
