import React, { useState, useEffect } from 'react';
import { db, storage } from '../firebase';
import { doc, getDoc } from 'firebase/firestore';
import { ref, getDownloadURL } from 'firebase/storage';

interface TeamLogoProps {
  team: string;
  className?: string;
  fallback?: React.ReactNode;
}

const TeamLogo: React.FC<TeamLogoProps> = ({ team, className = "w-12 h-12", fallback }) => {
  const [logoUrl, setLogoUrl] = useState<string | null>(null);
  const [error, setError] = useState(false);

  useEffect(() => {
    const fetchTeamLogo = async () => {
      try {
        // Önce Firestore'dan takım bilgisini al
        const teamRef = doc(db, 'teams', team);
        const teamDoc = await getDoc(teamRef);

        if (teamDoc.exists() && teamDoc.data().logoUrl) {
          setLogoUrl(teamDoc.data().logoUrl);
        } else {
          setError(true);
        }
      } catch (error) {
        console.error('Logo yüklenirken hata:', error);
        setError(true);
      }
    };

    fetchTeamLogo();
  }, [team]);

  if (error && fallback) {
    return <>{fallback}</>;
  }

  return (
    <div className={`${className} flex items-center justify-center`}>
      {logoUrl ? (
        <img 
          src={logoUrl}
          alt={`${team} logo`}
          className="w-full h-full object-contain"
          onError={() => setError(true)}
        />
      ) : (
        <div className="text-gray-500">...</div>
      )}
    </div>
  );
};

export default TeamLogo;
