import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface StatisticsItem {
  name: string;
  home: string;
  away: string;
  homeValue: number;
  awayValue: number;
  statisticsType: string;
}

interface MatchStatsProps {
  matchId: number;
}

const MatchStats = ({ matchId }: MatchStatsProps) => {
  const [stats, setStats] = useState<StatisticsItem[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await api.get(`/event/${matchId}/statistics`);
        const allStats = response.data.statistics[0].groups.flatMap(
          (group: any) => group.statisticsItems
        );
        setStats(allStats);
      } catch (error) {
        console.error('Maç istatistikleri yüklenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, [matchId]);

  const getStatName = (name: string) => {
    const translations: { [key: string]: string } = {
      'Ball possession': 'Topa Sahip Olma',
      'Total shots': 'Toplam Şut',
      'Shots on target': 'İsabetli Şut',
      'Shots off target': 'İsabetsiz Şut',
      'Blocked shots': 'Bloke Edilen Şut',
      'Corner kicks': 'Korner',
      'Offsides': 'Ofsayt',
      'Fouls': 'Faul',
      'Yellow cards': 'Sarı Kart',
      'Red cards': 'Kırmızı Kart',
      'Big chances': 'Net Pozisyon',
      'Goalkeeper saves': 'Kaleci Kurtarışı',
      'Passes': 'Pas',
      'Accurate passes': 'İsabetli Pas',
      'Long balls': 'Uzun Top',
      'Crosses': 'Orta',
      'Tackles': 'Müdahale',
      'Interceptions': 'Top Kapma',
      'Clearances': 'Top Uzaklaştırma',
      'Hit woodwork': 'Direkten Dönen',
    };
    return translations[name] || name;
  };

  if (loading) return <div className="text-center">Yükleniyor...</div>;

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold mb-4">Maç İstatistikleri</h3>
      <div className="grid gap-2">
        {stats.map((stat, index) => (
          <div key={index} className="grid grid-cols-11 items-center py-3 px-4 bg-white rounded-lg">
            {/* Sol Takım Değeri */}
            <div className="col-span-2 text-right font-bold">
              {stat.home}
            </div>

            {/* İstatistik Barı */}
            <div className="col-span-7 px-4">
              <div className="relative h-2 bg-gray-200 rounded-full">
                <div className="absolute inset-y-0 left-0 bg-blue-500 rounded-l-full"
                  style={{ 
                    width: `${(stat.homeValue / (stat.homeValue + stat.awayValue)) * 100}%`,
                    backgroundColor: stat.statisticsType === 'negative' ? '#ef4444' : '#3b82f6'
                  }}
                />
                <div className="absolute inset-y-0 right-0 bg-blue-500 rounded-r-full"
                  style={{ 
                    width: `${(stat.awayValue / (stat.homeValue + stat.awayValue)) * 100}%`,
                    backgroundColor: stat.statisticsType === 'negative' ? '#ef4444' : '#3b82f6'
                  }}
                />
              </div>
              <div className="text-center text-sm font-medium text-gray-600 mt-1">
                {getStatName(stat.name)}
              </div>
            </div>

            {/* Sağ Takım Değeri */}
            <div className="col-span-2 text-left font-bold">
              {stat.away}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MatchStats;
