import React, { useState, useEffect } from 'react';
import { auth, db } from '../firebase';
import { collection, addDoc, getDocs, updateDoc, doc } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { storage } from '../firebase';

interface Team {
  id?: string;
  name: string;
  slug: string;
  logoUrl: string;
  league: string;
}

const AdminPanel: React.FC = () => {
  const [teams, setTeams] = useState<Team[]>([]);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    slug: '',
    league: ''
  });

  useEffect(() => {
    fetchTeams();
  }, []);

  const fetchTeams = async () => {
    try {
      const teamsSnapshot = await getDocs(collection(db, 'teams'));
      const teamsData = teamsSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      })) as Team[];
      setTeams(teamsData);
    } catch (error) {
      console.error('Takımlar yüklenirken hata:', error);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files?.[0]) {
      setSelectedFile(e.target.files[0]);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (!selectedFile) {
        throw new Error('Lütfen bir logo seçin');
      }

      // Logo'yu storage'a yükle
      const storageRef = ref(storage, `team-logos/${formData.slug}`);
      await uploadBytes(storageRef, selectedFile);
      const logoUrl = await getDownloadURL(storageRef);

      // Takımı Firestore'a ekle
      await addDoc(collection(db, 'teams'), {
        ...formData,
        logoUrl
      });

      // Formu temizle
      setFormData({ name: '', slug: '', league: '' });
      setSelectedFile(null);
      
      // Takımları yeniden yükle
      fetchTeams();

    } catch (error) {
      console.error('Takım eklenirken hata:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 p-4">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-2xl font-bold text-white mb-8">Admin Panel</h1>

        {/* Takım Ekleme Formu */}
        <form onSubmit={handleSubmit} className="bg-gray-800 p-6 rounded-lg mb-8">
          <h2 className="text-xl text-white mb-4">Yeni Takım Ekle</h2>
          
          <div className="grid gap-4">
            <div>
              <label className="block text-gray-400 mb-1">Takım Adı</label>
              <input
                type="text"
                value={formData.name}
                onChange={e => setFormData({...formData, name: e.target.value})}
                className="w-full bg-gray-700 text-white px-4 py-2 rounded border border-gray-600"
                required
              />
            </div>

            <div>
              <label className="block text-gray-400 mb-1">Slug</label>
              <input
                type="text"
                value={formData.slug}
                onChange={e => setFormData({...formData, slug: e.target.value})}
                className="w-full bg-gray-700 text-white px-4 py-2 rounded border border-gray-600"
                required
              />
            </div>

            <div>
              <label className="block text-gray-400 mb-1">Lig</label>
              <input
                type="text"
                value={formData.league}
                onChange={e => setFormData({...formData, league: e.target.value})}
                className="w-full bg-gray-700 text-white px-4 py-2 rounded border border-gray-600"
                required
              />
            </div>

            <div>
              <label className="block text-gray-400 mb-1">Logo</label>
              <input
                type="file"
                onChange={handleFileChange}
                accept="image/*"
                className="w-full bg-gray-700 text-white px-4 py-2 rounded border border-gray-600"
                required
              />
            </div>

            <button
              type="submit"
              disabled={loading}
              className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600 disabled:opacity-50"
            >
              {loading ? 'Ekleniyor...' : 'Takım Ekle'}
            </button>
          </div>
        </form>

        {/* Takım Listesi */}
        <div className="bg-gray-800 p-6 rounded-lg">
          <h2 className="text-xl text-white mb-4">Takımlar</h2>
          
          <div className="grid gap-4">
            {teams.map(team => (
              <div key={team.id} className="flex items-center gap-4 bg-gray-700 p-4 rounded">
                <img 
                  src={team.logoUrl} 
                  alt={team.name}
                  className="w-12 h-12 object-contain"
                />
                <div>
                  <h3 className="text-white font-medium">{team.name}</h3>
                  <p className="text-gray-400 text-sm">{team.league}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPanel;
