import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import '../styles/match-detail.css'

interface Team {
  id: string
  name: string
  slug: string
  shortName: string
  sport: {
    name: string
  }
  teamColors: {
    primary: string
    secondary: string
  }
}

interface Event {
  homeTeam: Team
  awayTeam: Team
  status: {
    description: string
    type: string
  }
  homeScore: {
    current: number
  }
  awayScore: {
    current: number
  }
  tournament: {
    name: string
    uniqueTournament: {
      name: string
    }
  }
  venue: {
    stadium: {
      name: string
    }
  }
}

interface ApiResponse {
  event: Event
}

export default function MatchPage() {
  const { matchId } = useParams()
  const [matchData, setMatchData] = useState<Event | null>(null)
  const [isLoading, setIsLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  useEffect(() => {
    if (matchId) {
      fetchMatchDetails(matchId)
    }
  }, [matchId])

  const fetchMatchDetails = async (id: string) => {
    try {
      setIsLoading(true)
      setError(null)
      
      // Geçici mock data
      const mockData: ApiResponse = {
        event: {
          homeTeam: {
            id: "1715",
            name: "Stade Brestois",
            slug: "stade-brestois",
            shortName: "Brest",
            sport: {
              name: "Football"
            },
            teamColors: {
              primary: "#FF0000",
              secondary: "#FFFFFF"
            }
          },
          awayTeam: {
            id: "1716",
            name: "Paris Saint-Germain",
            slug: "psg",
            shortName: "PSG",
            sport: {
              name: "Football"
            },
            teamColors: {
              primary: "#000080",
              secondary: "#FF0000"
            }
          },
          status: {
            description: "Not started",
            type: "notstarted"
          },
          homeScore: {
            current: 0
          },
          awayScore: {
            current: 0
          },
          tournament: {
            name: "Ligue 1",
            uniqueTournament: {
              name: "French Ligue 1"
            }
          },
          venue: {
            stadium: {
              name: "Stade Francis-Le Blé"
            }
          }
        }
      }

      setMatchData(mockData.event)
      
    } catch (error) {
      console.error('Maç detayları alınamadı:', error)
      setError('Maç detayları yüklenirken bir hata oluştu')
    } finally {
      setIsLoading(false)
    }
  }

  const getTeamLogoUrl = (teamId: string) => {
    return `https://img.sofascore.com/api/v1/team/${teamId}/image`
  }

  if (isLoading) return <div className="loading">Yükleniyor...</div>
  if (error) return <div className="error">{error}</div>
  if (!matchData) return <div className="error">Maç bulunamadı</div>

  return (
    <div className="match-detail-container">
      <div className="match-header">
        <h1>{matchData.tournament.uniqueTournament.name}</h1>
        <h2>{matchData.venue.stadium.name}</h2>
      </div>

      <div className="team-logos">
        <div className="home-team">
          <img 
            src={getTeamLogoUrl(matchData.homeTeam.id)}
            alt={`${matchData.homeTeam.name} logo`}
            width={64}
            height={64}
            loading="eager"
          />
          <span>{matchData.homeTeam.name}</span>
        </div>
        
        <div className="score">
          <div className="current-score">
            {matchData.homeScore.current} - {matchData.awayScore.current}
          </div>
          <div className="match-status">
            {matchData.status.description}
          </div>
        </div>
        
        <div className="away-team">
          <img 
            src={getTeamLogoUrl(matchData.awayTeam.id)}
            alt={`${matchData.awayTeam.name} logo`}
            width={64}
            height={64}
            loading="eager"
          />
          <span>{matchData.awayTeam.name}</span>
        </div>
      </div>
    </div>
  )
}
