import React from 'react';
import { Link } from 'react-router-dom';
import { auth } from '../firebase';
import { useAuthState } from 'react-firebase-hooks/auth';

const Navbar = () => {
  const [user] = useAuthState(auth);

  return (
    <nav className="bg-gray-800 text-white p-4">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-xl font-bold">GOLAZO</Link>
        
        <div className="flex gap-4">
          <Link to="/matches" className="hover:text-gray-300">Maçlar</Link>
          <Link to="/live" className="hover:text-gray-300">Canlı Skorlar</Link>
          <Link to="/forum" className="hover:text-gray-300">Forum</Link>
          
          {user ? (
            <div className="flex gap-4">
              <Link to="/profile" className="hover:text-gray-300">Profil</Link>
              <button onClick={() => auth.signOut()} className="hover:text-gray-300">
                Çıkış Yap
              </button>
            </div>
          ) : (
            <Link to="/login" className="hover:text-gray-300">Giriş Yap</Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
