import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface Player {
  name: string;
  shortName: string;
  id: number;
}

interface MatchEvent {
  id: number;
  time: number;
  text: string;
  player?: Player;
  playerIn?: Player;
  playerOut?: Player;
  teamId: number;
  incidentType: string;
  isHome: boolean;
  reason?: string;
  goalScore?: {
    home: number;
    away: number;
  };
  assist1?: Player;
  incidentClass?: string;
  injury?: boolean;
  homeScore?: number;
  awayScore?: number;
  confirmed?: boolean;
}

interface MatchEventsProps {
  matchId: number;
  homeTeamId: number;
  awayTeamId: number;
}

const MatchEvents = ({ matchId, homeTeamId, awayTeamId }: MatchEventsProps) => {
  const [events, setEvents] = useState<MatchEvent[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get(`/event/${matchId}/incidents`);
        console.log('API Response:', response.data);
        const filteredEvents = response.data.incidents.filter((event: MatchEvent) => 
          ['goal', 'card', 'substitution', 'period', 'varDecision'].includes(event.incidentType)
        );
        setEvents(filteredEvents.reverse());
      } catch (error) {
        console.error('Ma√ß olaylarƒ± y√ºklenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, [matchId]);

  const getEventIcon = (type: string, incidentClass?: string) => {
    switch (type) {
      case 'goal':
        return '‚öΩ';
      case 'card':
        return incidentClass === 'yellow' ? 'üü®' : 
               incidentClass === 'red' ? 'üü•' : '‚¨ú';
      case 'substitution':
        return 'üîÑ';
      case 'varDecision':
        return 'üì∫';
      default:
        return '‚Ä¢';
    }
  };

  const getEventDescription = (event: MatchEvent) => {
    switch (event.incidentType) {
      case 'varDecision':
        return (
          <div>
            {event.incidentClass === 'goalAwarded' ? (
              <>
                <span className="font-bold">{event.player?.name}</span>
                <span className="text-gray-600"> gol√º VAR incelemesi sonrasƒ± onaylandƒ±</span>
              </>
            ) : event.incidentClass === 'goalCancelled' ? (
              <>
                <span className="font-bold">{event.player?.name}</span>
                <span className="text-gray-600"> gol√º VAR incelemesi sonrasƒ± iptal edildi</span>
              </>
            ) : (
              <span className="text-gray-600">VAR incelemesi</span>
            )}
          </div>
        );
      case 'period':
        if (event.text === 'HT') {
          return (
            <div className="text-center w-full">
              <div className="font-semibold">ƒ∞lk Yarƒ± Sonu</div>
              <div className="text-sm text-gray-500">
                {event.homeScore}-{event.awayScore}
              </div>
            </div>
          );
        } else if (event.text === 'FT') {
          return (
            <div className="text-center w-full">
              <div className="font-semibold">Ma√ß Sonu</div>
              <div className="text-sm text-gray-500">
                {event.homeScore}-{event.awayScore}
              </div>
            </div>
          );
        }
        return event.text;
      case 'goal':
        return (
          <div>
            <span className="font-bold">{event.player?.name}</span>
            <span className="text-gray-600"> gol attƒ± </span>
            {event.assist1 && (
              <div className="text-xs text-gray-500 mt-1">
                Asist: {event.assist1.name}
              </div>
            )}
            {event.goalScore && (
              <span className="text-sm text-gray-500">
                ({event.goalScore.home}-{event.goalScore.away})
              </span>
            )}
          </div>
        );
      case 'card':
        return (
          <div>
            <span className="font-bold">{event.player?.name}</span>
            <span className="text-gray-600">
              {event.incidentClass === 'yellow' ? ' sarƒ± kart g√∂rd√º' : 
               event.incidentClass === 'red' ? ' kƒ±rmƒ±zƒ± kart g√∂rd√º' : 
               ' kart g√∂rd√º'}
            </span>
            {event.reason && (
              <div className="text-xs text-gray-500 mt-1">
                Sebep: {event.reason}
              </div>
            )}
          </div>
        );
      case 'yellowRed':
        return (
          <div>
            <span className="font-bold">{event.player?.name}</span>
            <span className="text-gray-600"> ikinci sarƒ± karttan kƒ±rmƒ±zƒ± kart g√∂rd√º </span>
            {event.reason && (
              <span className="text-sm text-gray-500 italic">
                Sebep: {event.reason}
              </span>
            )}
            <div className="text-xs text-gray-500 mt-1">
              ƒ∞kinci sarƒ± kartƒ± g√∂rd√ºƒü√º i√ßin oyundan atƒ±ldƒ±
            </div>
          </div>
        );
      case 'substitution':
        return (
          <div>
            <div>
              <span className="font-bold text-green-600">{event.playerIn?.name}</span>
              <span className="text-gray-600"> oyuna girdi</span>
              {event.injury && (
                <span className="text-xs text-red-500 ml-2">(Sakatlƒ±k deƒüi≈üikliƒüi)</span>
              )}
            </div>
            <div>
              <span className="font-bold text-red-600">{event.playerOut?.name}</span>
              <span className="text-gray-600"> oyundan √ßƒ±ktƒ±</span>
            </div>
          </div>
        );
      default:
        return event.text;
    }
  };

  if (loading) return <div className="text-center">Y√ºkleniyor...</div>;

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold mb-4">Ma√ß Olaylarƒ±</h3>
      <div className="flex flex-col space-y-2">
        {events.map((event) => (
          <div 
            key={event.id} 
            className={`flex items-center gap-2 p-3 rounded-lg ${
              event.incidentType === 'period' ? 'justify-center bg-gray-100' :
              event.isHome ? 'justify-start' : 'justify-end'
            } ${
              event.incidentType === 'goal' ? 'bg-green-50' :
              event.incidentType === 'card' ? (
                event.incidentClass === 'yellow' ? 'bg-yellow-50' :
                event.incidentClass === 'red' ? 'bg-red-50' : 'bg-gray-50'
              ) : 
              event.incidentType === 'varDecision' ? 'bg-blue-50' :
              event.incidentType !== 'period' ? 'bg-gray-50' : ''
            }`}
          >
            {event.incidentType === 'period' ? (
              getEventDescription(event)
            ) : event.isHome ? (
              <>
                <span className="text-lg">{getEventIcon(event.incidentType, event.incidentClass)}</span>
                <div>
                  {getEventDescription(event)}
                  <span className="text-sm text-gray-500">{event.time}'</span>
                </div>
              </>
            ) : (
              <>
                <div className="text-right">
                  {getEventDescription(event)}
                  <span className="text-sm text-gray-500">{event.time}'</span>
                </div>
                <span className="text-lg">{getEventIcon(event.incidentType, event.incidentClass)}</span>
              </>
            )}
          </div>
        ))}

        {events.length === 0 && (
          <p className="text-center text-gray-500">
            Hen√ºz ma√ß olayƒ± bulunmuyor.
          </p>
        )}
      </div>
    </div>
  );
};

export default MatchEvents;
