import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface StatItem {
  name: string;
  home: string;
  away: string;
  homeValue: number;
  awayValue: number;
  compareCode: number;
}

interface MatchStatsProps {
  matchId: number;
}

const StatBar = ({ home, away, label }: { home: number; away: number; label: string }) => {
  const total = home + away;
  const homePercent = total > 0 ? (home / total) * 100 : 0;
  const awayPercent = total > 0 ? (away / total) * 100 : 0;

  return (
    <div className="flex flex-col gap-2">
      <div className="flex justify-between text-xs text-gray-400">
        <span>{home}</span>
        <span>{label}</span>
        <span>{away}</span>
      </div>
      <div className="flex h-2 w-full bg-gray-800 rounded-full overflow-hidden">
        <div 
          style={{ width: `${homePercent}%` }} 
          className="bg-blue-500 h-full"
        />
        <div 
          style={{ width: `${awayPercent}%` }} 
          className="bg-red-500 h-full"
        />
      </div>
    </div>
  );
};

const MatchStats: React.FC<MatchStatsProps> = ({ matchId }) => {
  const [stats, setStats] = useState<StatItem[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await api.get(`/event/${matchId}/statistics`);
        const matchStats = response.data.statistics[0].groups
          .find((g: any) => g.groupName === 'Şutlar')
          ?.statisticsItems || [];
        setStats(matchStats);
      } catch (error) {
        console.error('İstatistikler yüklenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, [matchId]);

  if (loading) {
    return <div className="text-center text-gray-400 py-8">Yükleniyor...</div>;
  }

  return (
    <div className="flex justify-center">
      <div className="bg-gray-900 rounded-lg text-sm w-full max-w-lg p-4">
        <h3 className="text-lg font-medium text-white mb-4">Şutlar</h3>
        <div className="space-y-4">
          {stats.map((stat, index) => (
            <StatBar
              key={index}
              home={stat.homeValue}
              away={stat.awayValue}
              label={stat.name}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default MatchStats;
