import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface StatItem {
  name: string;
  home: number;
  away: number;
}

interface MatchStatsProps {
  matchId: number;
}

const MatchStats: React.FC<MatchStatsProps> = ({ matchId }) => {
  const [stats, setStats] = useState<StatItem[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await api.get(`/event/${matchId}/statistics`);
        const statistics = response.data.statistics;

        // İstatistikleri düzenle
        const formattedStats = statistics.map((stat: any) => ({
          name: stat.groupName,
          home: stat.home || 0,
          away: stat.away || 0,
        }));

        setStats(formattedStats);
      } catch (error) {
        console.error('İstatistikler yüklenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, [matchId]);

  if (loading) {
    return <div className="text-center text-gray-400 py-4">İstatistikler yükleniyor...</div>;
  }

  if (stats.length === 0) {
    return <div className="text-center text-gray-400 py-4">İstatistik bulunamadı</div>;
  }

  const getPercentage = (home: number, away: number) => {
    const total = home + away;
    if (total === 0) return 50;
    return (home / total) * 100;
  };

  return (
    <div className="space-y-4">
      {stats.map((stat, index) => (
        <div key={index} className="bg-gray-800/50 rounded-lg p-4">
          <div className="flex justify-between text-sm text-gray-400 mb-2">
            <span>{stat.home}</span>
            <span className="font-medium text-white">{stat.name}</span>
            <span>{stat.away}</span>
          </div>
          <div className="h-2 bg-gray-700 rounded-full overflow-hidden">
            <div
              className="h-full bg-blue-500"
              style={{ width: `${getPercentage(stat.home, stat.away)}%` }}
            />
          </div>
        </div>
      ))}
    </div>
  );
};

export default MatchStats;
