import React, { useState, useEffect } from 'react';
import { auth, db, storage } from '../firebase';
import { collection, getDocs, updateDoc, doc, getDoc, setDoc } from 'firebase/firestore';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import TeamLogo from '../components/TeamLogo';

interface Match {
  id: number;
  homeTeam: { 
    name: string;
    slug: string;
  };
  awayTeam: { 
    name: string;
    slug: string;
  };
  tournament: {
    name: string;
    category: {
      name: string;
    };
  };
  startTimestamp: number;
}

const AdminPanel: React.FC = () => {
  const [matches, setMatches] = useState<Match[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedTeam, setSelectedTeam] = useState<any>(null);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    fetchMatches();
  }, []);

  const fetchMatches = async () => {
    try {
      const response = await fetch('https://www.sofascore.com/api/v1/sport/football/scheduled-events/2024-02-13');
      const data = await response.json();
      setMatches(data.events);
    } catch (error) {
      console.error('Maçlar yüklenirken hata:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTeamSelect = (team: any) => {
    setSelectedTeam(team);
    setEditMode(true);
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files?.[0]) {
      setSelectedFile(e.target.files[0]);
    }
  };

  const handleLogoUpdate = async () => {
    if (!selectedFile || !selectedTeam) return;

    try {
      setLoading(true);

      // Dosya adını düzenle
      const fileName = `${selectedTeam.slug}.png`;
      
      // Storage referansı
      const storageRef = ref(storage, `logos/${fileName}`);

      // Dosyayı küçült
      const compressedFile = await compressImage(selectedFile);

      // Yükle
      const uploadResult = await uploadBytes(storageRef, compressedFile);
      console.log('Upload başarılı:', uploadResult);

      // URL al
      const logoUrl = await getDownloadURL(storageRef);
      console.log('Logo URL:', logoUrl);

      // Firestore'da takım dokümanını kontrol et ve oluştur/güncelle
      const teamRef = doc(db, 'teams', selectedTeam.slug);
      const teamDoc = await getDoc(teamRef);

      if (teamDoc.exists()) {
        // Doküman varsa güncelle
        await updateDoc(teamRef, {
          logoUrl,
          updatedAt: new Date()
        });
      } else {
        // Doküman yoksa oluştur
        await setDoc(teamRef, {
          name: selectedTeam.name,
          slug: selectedTeam.slug,
          logoUrl,
          createdAt: new Date(),
          updatedAt: new Date()
        });
      }

      alert('Logo başarıyla güncellendi!');
      setEditMode(false);
      setSelectedTeam(null);
      setSelectedFile(null);
    } catch (error: any) {
      console.error('Logo güncellenirken hata:', error);
      alert(`Hata: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Resim sıkıştırma fonksiyonu
  const compressImage = async (file: File): Promise<Blob> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = (event) => {
        const img = new Image();
        img.src = event.target?.result as string;
        img.onload = () => {
          const canvas = document.createElement('canvas');
          const MAX_WIDTH = 512;
          const MAX_HEIGHT = 512;
          let width = img.width;
          let height = img.height;

          if (width > height) {
            if (width > MAX_WIDTH) {
              height *= MAX_WIDTH / width;
              width = MAX_WIDTH;
            }
          } else {
            if (height > MAX_HEIGHT) {
              width *= MAX_HEIGHT / height;
              height = MAX_HEIGHT;
            }
          }

          canvas.width = width;
          canvas.height = height;

          const ctx = canvas.getContext('2d');
          ctx?.drawImage(img, 0, 0, width, height);

          canvas.toBlob(
            (blob) => {
              if (blob) {
                resolve(blob);
              } else {
                reject(new Error('Canvas to Blob conversion failed'));
              }
            },
            'image/png',
            0.8
          );
        };
      };
      reader.onerror = (error) => reject(error);
    });
  };

  return (
    <div className="min-h-screen bg-gray-900 p-4">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-2xl font-bold text-white mb-8">Admin Panel</h1>

        {editMode && selectedTeam ? (
          // Takım Düzenleme Formu
          <div className="bg-gray-800 p-6 rounded-lg mb-8">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-xl text-white">
                {selectedTeam.name} - Logo Düzenle
              </h2>
              <button
                onClick={() => setEditMode(false)}
                className="text-gray-400 hover:text-white"
              >
                ✕
              </button>
            </div>

            <div className="flex items-center gap-8">
              <div className="w-32 h-32 bg-gray-700 rounded-lg flex items-center justify-center">
                <TeamLogo 
                  team={selectedTeam.slug}
                  className="w-24 h-24"
                  fallback={
                    <div className="text-gray-400">Logo Yok</div>
                  }
                />
              </div>

              <div className="flex-1">
                <input
                  type="file"
                  onChange={handleFileChange}
                  accept="image/*"
                  className="w-full bg-gray-700 text-white px-4 py-2 rounded border border-gray-600 mb-4"
                />

                <button
                  onClick={handleLogoUpdate}
                  disabled={!selectedFile || loading}
                  className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600 disabled:opacity-50"
                >
                  {loading ? 'Güncelleniyor...' : 'Logo Güncelle'}
                </button>
              </div>
            </div>
          </div>
        ) : (
          // Maç Listesi
          <div className="grid gap-4">
            {matches.map(match => (
              <div key={match.id} className="bg-gray-800 p-4 rounded-lg">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-4">
                    <div className="flex flex-col items-center">
                      <TeamLogo 
                        team={match.homeTeam.slug}
                        className="w-12 h-12"
                      />
                      <button
                        onClick={() => handleTeamSelect(match.homeTeam)}
                        className="text-xs text-blue-400 hover:text-blue-300 mt-1"
                      >
                        Düzenle
                      </button>
                    </div>

                    <span className="text-white font-medium">
                      {match.homeTeam.name}
                    </span>

                    <span className="text-gray-400 mx-4">vs</span>

                    <span className="text-white font-medium">
                      {match.awayTeam.name}
                    </span>

                    <div className="flex flex-col items-center">
                      <TeamLogo 
                        team={match.awayTeam.slug}
                        className="w-12 h-12"
                      />
                      <button
                        onClick={() => handleTeamSelect(match.awayTeam)}
                        className="text-xs text-blue-400 hover:text-blue-300 mt-1"
                      >
                        Düzenle
                      </button>
                    </div>
                  </div>

                  <div className="text-right">
                    <div className="text-sm text-gray-400">
                      {match.tournament.category.name}
                    </div>
                    <div className="text-white">
                      {match.tournament.name}
                    </div>
                    <div className="text-sm text-gray-400">
                      {new Date(match.startTimestamp * 1000).toLocaleString('tr-TR')}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
