import React, { useState } from 'react';
import { format, addDays, subDays, startOfMonth, endOfMonth, startOfWeek, endOfWeek, isSameDay, isSameMonth } from 'date-fns';
import { tr } from 'date-fns/locale';

interface DateSelectorProps {
  selectedDate: Date;
  onChange: (date: Date) => void;
}

const DateSelector: React.FC<DateSelectorProps> = ({ selectedDate, onChange }) => {
  const [showCalendar, setShowCalendar] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(selectedDate);

  const goToPreviousDay = () => onChange(subDays(selectedDate, 1));
  const goToNextDay = () => onChange(addDays(selectedDate, 1));

  const previousDay = format(subDays(selectedDate, 1), 'd MMMM', { locale: tr });
  const nextDay = format(addDays(selectedDate, 1), 'd MMMM', { locale: tr });

  const onDateClick = (day: Date) => {
    onChange(day);
    setShowCalendar(false);
  };

  const renderHeader = () => {
    return (
      <div className="flex justify-between items-center mb-2">
        <button
          onClick={() => setCurrentMonth(subDays(currentMonth, 30))}
          className="text-gray-400 hover:text-white p-1"
        >
          ◀
        </button>
        <span className="text-white font-medium">
          {format(currentMonth, 'MMMM yyyy', { locale: tr })}
        </span>
        <button
          onClick={() => setCurrentMonth(addDays(currentMonth, 30))}
          className="text-gray-400 hover:text-white p-1"
        >
          ▶
        </button>
      </div>
    );
  };

  const renderDays = () => {
    const days = ['Pzt', 'Sal', 'Çar', 'Per', 'Cum', 'Cmt', 'Paz'];
    return (
      <div className="grid grid-cols-7 gap-1 mb-1">
        {days.map(day => (
          <div key={day} className="text-gray-400 text-center text-sm py-1">
            {day}
          </div>
        ))}
      </div>
    );
  };

  const renderCells = () => {
    const monthStart = startOfMonth(currentMonth);
    const monthEnd = endOfMonth(monthStart);
    const startDate = startOfWeek(monthStart, { weekStartsOn: 1 });
    const endDate = endOfWeek(monthEnd);

    const rows = [];
    let days = [];
    let day = startDate;

    while (day <= endDate) {
      for (let i = 0; i < 7; i++) {
        const cloneDay = day;
        days.push(
          <button
            key={day.toString()}
            onClick={() => onDateClick(cloneDay)}
            className={`
              p-2 text-sm rounded-lg transition-colors
              ${isSameMonth(day, monthStart) ? 'text-white' : 'text-gray-600'}
              ${isSameDay(day, selectedDate) ? 'bg-blue-600' : 'hover:bg-gray-700'}
            `}
          >
            {format(day, 'd')}
          </button>
        );
        day = addDays(day, 1);
      }
      rows.push(
        <div key={day.toString()} className="grid grid-cols-7 gap-1">
          {days}
        </div>
      );
      days = [];
    }
    return rows;
  };

  return (
    <div className="flex justify-center items-center w-full max-w-2xl mx-auto">
      <button
        onClick={goToPreviousDay}
        className="flex items-center gap-2 px-4 py-2 text-gray-400 hover:text-white hover:bg-gray-800 rounded-lg transition-all"
      >
        <span>◀</span>
        <span className="text-sm">{previousDay}</span>
      </button>

      <div className="relative mx-8">
        <button
          onClick={() => setShowCalendar(!showCalendar)}
          className="px-8 py-2 bg-gray-800 rounded-lg text-center hover:bg-gray-700 transition-all"
        >
          <div className="text-sm text-gray-400">
            {format(selectedDate, 'EEEE', { locale: tr })}
          </div>
          <div className="text-lg text-white font-medium">
            {format(selectedDate, 'd MMMM yyyy', { locale: tr })}
          </div>
        </button>

        {showCalendar && (
          <div className="absolute top-full mt-2 left-1/2 -translate-x-1/2 bg-gray-800 rounded-lg shadow-lg p-4 z-10">
            {renderHeader()}
            {renderDays()}
            {renderCells()}
          </div>
        )}
      </div>

      <button
        onClick={goToNextDay}
        className="flex items-center gap-2 px-4 py-2 text-gray-400 hover:text-white hover:bg-gray-800 rounded-lg transition-all"
      >
        <span className="text-sm">{nextDay}</span>
        <span>▶</span>
      </button>
    </div>
  );
};

export default DateSelector;
