import React, { useState } from 'react';
import { format, addDays, subDays } from 'date-fns';
import { tr } from 'date-fns/locale';

interface DateSelectorProps {
  selectedDate: Date;
  onChange: (date: Date) => void;
}

const DateSelector: React.FC<DateSelectorProps> = ({ selectedDate, onChange }) => {
  const [showDatePicker, setShowDatePicker] = useState(false);

  const goToPreviousDay = () => {
    onChange(subDays(selectedDate, 1));
  };

  const goToNextDay = () => {
    onChange(addDays(selectedDate, 1));
  };

  const previousDay = format(subDays(selectedDate, 1), 'd MMMM', { locale: tr });
  const nextDay = format(addDays(selectedDate, 1), 'd MMMM', { locale: tr });

  return (
    <div className="flex justify-center items-center w-full max-w-2xl mx-auto">
      <button
        onClick={goToPreviousDay}
        className="flex items-center gap-2 px-4 py-2 text-gray-400 hover:text-white hover:bg-gray-800 rounded-lg transition-all"
      >
        <span>◀</span>
        <span className="text-sm">{previousDay}</span>
      </button>

      <div className="relative mx-8">
        <button
          onClick={() => setShowDatePicker(!showDatePicker)}
          className="px-8 py-2 bg-gray-800 rounded-lg text-center hover:bg-gray-700 transition-all"
        >
          <div className="text-sm text-gray-400">
            {format(selectedDate, 'EEEE', { locale: tr })}
          </div>
          <div className="text-lg text-white font-medium">
            {format(selectedDate, 'd MMMM yyyy', { locale: tr })}
          </div>
        </button>

        {showDatePicker && (
          <div className="absolute top-full mt-2 left-1/2 -translate-x-1/2 bg-gray-800 rounded-lg shadow-lg p-2 z-10">
            <input
              type="date"
              value={format(selectedDate, 'yyyy-MM-dd')}
              onChange={(e) => {
                onChange(new Date(e.target.value));
                setShowDatePicker(false);
              }}
              className="bg-gray-700 text-white px-4 py-2 rounded border border-gray-600 hover:border-gray-500 focus:outline-none focus:border-blue-500 transition-all"
            />
          </div>
        )}
      </div>

      <button
        onClick={goToNextDay}
        className="flex items-center gap-2 px-4 py-2 text-gray-400 hover:text-white hover:bg-gray-800 rounded-lg transition-all"
      >
        <span className="text-sm">{nextDay}</span>
        <span>▶</span>
      </button>
    </div>
  );
};

export default DateSelector;
