import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface MatchDetails {
  homeTeam: {
    name: string;
    shortName: string;
    score: number;
  };
  awayTeam: {
    name: string;
    shortName: string;
    score: number;
  };
  tournament: {
    name: string;
    uniqueTournament: {
      name: string;
    };
  };
  status: {
    description: string;
    type: string;
  };
  venue?: {
    name: string;
    city: string;
  };
}

interface MatchStats {
  statistics: {
    period: string;
    groups: {
      groupName: string;
      statisticsItems: {
        name: string;
        home: string;
        away: string;
        homeValue: number;
        awayValue: number;
        compareCode: number;
        statisticsType: string;
      }[];
    }[];
  }[];
}

const MatchDetails = ({ matchId }: { matchId: number }) => {
  const [matchInfo, setMatchInfo] = useState<MatchDetails | null>(null);
  const [stats, setStats] = useState<MatchStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState<'overview' | 'detailed'>('overview');

  useEffect(() => {
    const fetchMatchData = async () => {
      setLoading(true);
      try {
        const [matchResponse, statsResponse] = await Promise.all([
          api.get(`/event/${matchId}`),
          api.get(`/event/${matchId}/statistics`)
        ]);
        setMatchInfo(matchResponse.data);
        setStats(statsResponse.data);
      } catch (error) {
        console.error('Maç bilgileri yüklenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchMatchData();
  }, [matchId]);

  if (loading || !matchInfo) return <div className="text-center">Yükleniyor...</div>;

  const overviewStats = stats?.statistics[0].groups.find(g => g.groupName === 'Match overview')?.statisticsItems || [];
  const allStats = stats?.statistics[0].groups || [];

  const StatBar = ({ value1, value2, type }: { value1: number; value2: number; type: string }) => {
    const total = value1 + value2;
    const percent1 = total === 0 ? 50 : (value1 / total) * 100;
    const percent2 = total === 0 ? 50 : (value2 / total) * 100;
    const isNegative = type === 'negative';

    return (
      <div className="h-2 w-full bg-gray-200 rounded-full overflow-hidden">
        <div className="flex">
          <div 
            style={{ width: `${percent1}%` }} 
            className={`h-2 ${isNegative ? 'bg-red-500' : 'bg-blue-500'}`}
          />
          <div 
            style={{ width: `${percent2}%` }} 
            className={`h-2 ${isNegative ? 'bg-red-300' : 'bg-blue-300'}`}
          />
        </div>
      </div>
    );
  };

  return (
    <div className="bg-white rounded-lg">
      {/* Maç Başlığı */}
      <div className="p-6 border-b">
        <div className="text-center mb-4">
          <p className="text-sm text-gray-600">{matchInfo.tournament.uniqueTournament.name}</p>
          <p className="text-xs text-gray-500">{matchInfo.venue?.name}, {matchInfo.venue?.city}</p>
        </div>
        <div className="flex justify-between items-center">
          <div className="text-center flex-1">
            <p className="font-bold text-lg">{matchInfo.homeTeam.name}</p>
          </div>
          <div className="text-center px-4">
            <div className="text-2xl font-bold mb-1">
              {matchInfo.homeTeam.score} - {matchInfo.awayTeam.score}
            </div>
            <div className="text-sm text-red-600 font-medium">
              {matchInfo.status.description}
            </div>
          </div>
          <div className="text-center flex-1">
            <p className="font-bold text-lg">{matchInfo.awayTeam.name}</p>
          </div>
        </div>
      </div>

      {/* İstatistikler */}
      <div className="p-6">
        <div className="flex gap-4 mb-6">
          <button
            onClick={() => setActiveTab('overview')}
            className={`px-4 py-2 rounded ${activeTab === 'overview' ? 'bg-blue-500 text-white' : 'bg-gray-100'}`}
          >
            Genel İstatistikler
          </button>
          <button
            onClick={() => setActiveTab('detailed')}
            className={`px-4 py-2 rounded ${activeTab === 'detailed' ? 'bg-blue-500 text-white' : 'bg-gray-100'}`}
          >
            Detaylı İstatistikler
          </button>
        </div>

        {stats ? (
          activeTab === 'overview' ? (
            <div className="space-y-4">
              {overviewStats.map((stat, idx) => (
                <div key={idx} className="flex flex-col gap-2">
                  <div className="flex justify-between text-sm text-gray-600">
                    <span>{stat.home}</span>
                    <span className="font-medium">{stat.name}</span>
                    <span>{stat.away}</span>
                  </div>
                  <StatBar 
                    value1={stat.homeValue} 
                    value2={stat.awayValue}
                    type={stat.statisticsType}
                  />
                </div>
              ))}
            </div>
          ) : (
            <div className="space-y-8">
              {allStats.map((group, idx) => (
                <div key={idx} className="border-t pt-4 first:border-t-0 first:pt-0">
                  <h3 className="text-lg font-semibold mb-4">{group.groupName}</h3>
                  <div className="space-y-4">
                    {group.statisticsItems.map((stat, statIdx) => (
                      <div key={statIdx} className="flex flex-col gap-2">
                        <div className="flex justify-between text-sm text-gray-600">
                          <span>{stat.home}</span>
                          <span className="font-medium">{stat.name}</span>
                          <span>{stat.away}</span>
                        </div>
                        <StatBar 
                          value1={stat.homeValue} 
                          value2={stat.awayValue}
                          type={stat.statisticsType}
                        />
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          )
        ) : (
          <div className="text-center text-gray-500">
            Bu maç için henüz istatistik bulunmuyor.
          </div>
        )}
      </div>
    </div>
  );
};

export default MatchDetails;
