import React, { useState } from 'react';

interface TeamLogoProps {
  team: string;  // slug olarak alÄ±yoruz
  className?: string;
}

const TeamLogo: React.FC<TeamLogoProps> = ({ team, className = "w-12 h-12" }) => {
  const [error, setError] = useState(false);

  if (error || !team) {
    return (
      <div className={`${className} flex items-center justify-center bg-gray-700 rounded`}>
        <span className="text-white text-sm font-medium">?</span>
      </div>
    );
  }

  return (
    <div className={`${className} flex items-center justify-center`}>
      <img 
        src={`https://img.sofascore.com/api/v1/team/${team}/image`}
        alt="team logo"
        className="w-full h-full object-contain"
        onError={() => setError(true)}
      />
    </div>
  );
};

export default TeamLogo;
