import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface Player {
  name: string;
  shortName: string;
  id: number;
}

interface MatchEvent {
  id: number;
  time: number;
  text: string;
  player?: Player;
  playerIn?: Player;
  playerOut?: Player;
  teamId: number;
  incidentType: string;
  isHome: boolean;
  reason?: string;
  goalScore?: {
    home: number;
    away: number;
  };
}

interface MatchEventsProps {
  matchId: number;
  homeTeamId: number;
  awayTeamId: number;
}

const MatchEvents = ({ matchId, homeTeamId, awayTeamId }: MatchEventsProps) => {
  const [events, setEvents] = useState<MatchEvent[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get(`/event/${matchId}/incidents`);
        console.log('API Response:', response.data);
        const filteredEvents = response.data.incidents.filter((event: MatchEvent) => 
          ['goal', 'card', 'yellowCard', 'redCard', 'yellowRed', 'substitution'].includes(event.incidentType)
        );
        setEvents(filteredEvents.reverse()); // Son olaylarÄ± baÅŸta gÃ¶ster
      } catch (error) {
        console.error('MaÃ§ olaylarÄ± yÃ¼klenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, [matchId]);

  const getEventIcon = (type: string) => {
    switch (type) {
      case 'goal':
        return 'âš½';
      case 'yellowCard':
        return 'ðŸŸ¨';
      case 'redCard':
        return 'ðŸŸ¥';
      case 'yellowRed':
        return 'ðŸŸ¨ðŸŸ¥';
      case 'substitution':
        return 'ðŸ”„';
      default:
        return 'â€¢';
    }
  };

  const getEventDescription = (event: MatchEvent) => {
    switch (event.incidentType) {
      case 'goal':
        return (
          <div>
            <span className="font-bold">{event.player?.name}</span>
            <span className="text-gray-600"> gol attÄ± </span>
            {event.goalScore && (
              <span className="text-sm text-gray-500">
                ({event.goalScore.home}-{event.goalScore.away})
              </span>
            )}
          </div>
        );
      case 'yellowCard':
        return (
          <div>
            <span className="font-bold">{event.player?.name}</span>
            <span className="text-gray-600"> sarÄ± kart gÃ¶rdÃ¼ </span>
            {event.reason && (
              <span className="text-sm text-gray-500 italic">
                Sebep: {event.reason}
              </span>
            )}
            <div className="text-xs text-gray-500 mt-1">
              Faul yaptÄ±ÄŸÄ± iÃ§in sarÄ± kart gÃ¶rdÃ¼
            </div>
          </div>
        );
      case 'redCard':
        return (
          <div>
            <span className="font-bold">{event.player?.name}</span>
            <span className="text-gray-600"> kÄ±rmÄ±zÄ± kart gÃ¶rdÃ¼ </span>
            {event.reason && (
              <span className="text-sm text-gray-500 italic">
                Sebep: {event.reason}
              </span>
            )}
            <div className="text-xs text-gray-500 mt-1">
              Sert faul yaptÄ±ÄŸÄ± iÃ§in direkt kÄ±rmÄ±zÄ± kart gÃ¶rdÃ¼
            </div>
          </div>
        );
      case 'yellowRed':
        return (
          <div>
            <span className="font-bold">{event.player?.name}</span>
            <span className="text-gray-600"> ikinci sarÄ± karttan kÄ±rmÄ±zÄ± kart gÃ¶rdÃ¼ </span>
            {event.reason && (
              <span className="text-sm text-gray-500 italic">
                Sebep: {event.reason}
              </span>
            )}
            <div className="text-xs text-gray-500 mt-1">
              Ä°kinci sarÄ± kartÄ± gÃ¶rdÃ¼ÄŸÃ¼ iÃ§in oyundan atÄ±ldÄ±
            </div>
          </div>
        );
      case 'substitution':
        return (
          <div>
            <div>
              <span className="font-bold text-green-600">{event.playerIn?.name}</span>
              <span className="text-gray-600"> oyuna girdi</span>
            </div>
            <div>
              <span className="font-bold text-red-600">{event.playerOut?.name}</span>
              <span className="text-gray-600"> oyundan Ã§Ä±ktÄ±</span>
            </div>
          </div>
        );
      default:
        return event.text;
    }
  };

  if (loading) return <div className="text-center">YÃ¼kleniyor...</div>;

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold mb-4">MaÃ§ OlaylarÄ±</h3>
      <div className="flex flex-col space-y-2">
        {events.map((event) => (
          <div 
            key={event.id} 
            className={`flex items-center gap-2 p-3 rounded-lg ${
              event.isHome ? 'justify-start' : 'justify-end'
            } ${
              event.incidentType === 'goal' ? 'bg-green-50' :
              event.incidentType.includes('card') ? 'bg-red-50' :
              'bg-gray-50'
            }`}
          >
            {event.isHome ? (
              <>
                <span className="text-lg">{getEventIcon(event.incidentType)}</span>
                <div>
                  {getEventDescription(event)}
                  <span className="text-sm text-gray-500">{event.time}'</span>
                </div>
              </>
            ) : (
              <>
                <div className="text-right">
                  {getEventDescription(event)}
                  <span className="text-sm text-gray-500">{event.time}'</span>
                </div>
                <span className="text-lg">{getEventIcon(event.incidentType)}</span>
              </>
            )}
          </div>
        ))}

        {events.length === 0 && (
          <p className="text-center text-gray-500">
            HenÃ¼z maÃ§ olayÄ± bulunmuyor.
          </p>
        )}
      </div>
    </div>
  );
};

export default MatchEvents;
