import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface StatisticsItem {
  name: string;
  home: string;
  away: string;
  homeValue: number;
  awayValue: number;
  statisticsType: string;
}

interface MatchStatsProps {
  matchId: number;
}

const MatchStats = ({ matchId }: MatchStatsProps) => {
  const [stats, setStats] = useState<StatisticsItem[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await api.get(`/event/${matchId}/statistics`);
        const allStats = response.data.statistics[0].groups[0].statisticsItems;
        setStats(allStats);
      } catch (error) {
        console.error('MaÃ§ istatistikleri yÃ¼klenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, [matchId]);

  const getStatIcon = (name: string) => {
    switch (name) {
      case 'Yellow cards':
        return 'ðŸŸ¨';
      case 'Red cards':
        return 'ðŸŸ¥';
      case 'Ball possession':
        return 'âš½';
      case 'Total shots':
        return 'ðŸŽ¯';
      case 'Corner kicks':
        return 'ðŸš©';
      default:
        return null;
    }
  };

  const renderStatBar = (home: number, away: number, type: string) => {
    const total = home + away;
    const homeWidth = total === 0 ? 50 : (home / total) * 100;
    const awayWidth = total === 0 ? 50 : (away / total) * 100;

    return (
      <div className="flex h-2 bg-gray-200 rounded overflow-hidden">
        <div 
          className={`${type === 'negative' ? 'bg-red-500' : 'bg-blue-500'}`}
          style={{ width: `${homeWidth}%` }}
        />
        <div 
          className={`${type === 'negative' ? 'bg-red-500' : 'bg-blue-500'}`}
          style={{ width: `${awayWidth}%` }}
        />
      </div>
    );
  };

  if (loading) return <div className="text-center">YÃ¼kleniyor...</div>;

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold mb-4">MaÃ§ Ä°statistikleri</h3>
      <div className="space-y-4">
        {stats.map((stat, index) => {
          const icon = getStatIcon(stat.name);
          if (!icon) return null;

          return (
            <div key={index} className="bg-white p-4 rounded-lg shadow">
              <div className="flex justify-between items-center mb-2">
                <div className="flex items-center gap-2">
                  <span className="text-lg">{icon}</span>
                  <span className="font-medium">{stat.name}</span>
                </div>
                <div className="flex gap-4">
                  <span className="font-bold">{stat.home}</span>
                  <span className="text-gray-400">-</span>
                  <span className="font-bold">{stat.away}</span>
                </div>
              </div>
              {renderStatBar(stat.homeValue, stat.awayValue, stat.statisticsType)}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MatchStats;
