import React, { useState, useEffect } from 'react';
import { api } from '../services/api';

interface MatchEvent {
  id: number;
  time: number;
  text: string;
  player?: {
    name: string;
    shortName: string;
  };
  playerIn?: {
    name: string;
    shortName: string;
  };
  playerOut?: {
    name: string;
    shortName: string;
  };
  teamId: number;
  incidentType: string;
  isHome: boolean;
}

interface MatchEventsProps {
  matchId: number;
  homeTeamId: number;
  awayTeamId: number;
}

const MatchEvents = ({ matchId, homeTeamId, awayTeamId }: MatchEventsProps) => {
  const [events, setEvents] = useState<MatchEvent[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await api.get(`/event/${matchId}/incidents`);
        console.log('API Response:', response.data); // Debug için
        const filteredEvents = response.data.incidents.filter((event: MatchEvent) => 
          ['goal', 'card', 'yellowCard', 'redCard', 'yellowRed', 'substitution'].includes(event.incidentType)
        );
        setEvents(filteredEvents);
      } catch (error) {
        console.error('Maç olayları yüklenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, [matchId]);

  const getEventIcon = (type: string) => {
    switch (type) {
      case 'goal':
        return '⚽';
      case 'yellowCard':
        return '🟨';
      case 'redCard':
        return '🟥';
      case 'yellowRed':
        return '🟨🟥';
      case 'substitution':
        return '🔄';
      default:
        return '•';
    }
  };

  const getEventText = (event: MatchEvent) => {
    switch (event.incidentType) {
      case 'goal':
        return `${event.player?.name || 'Bilinmeyen Oyuncu'} (Gol)`;
      case 'yellowCard':
        return `${event.player?.name || 'Bilinmeyen Oyuncu'} (Sarı Kart)`;
      case 'redCard':
        return `${event.player?.name || 'Bilinmeyen Oyuncu'} (Kırmızı Kart)`;
      case 'yellowRed':
        return `${event.player?.name || 'Bilinmeyen Oyuncu'} (İkinci Sarı Kart)`;
      case 'substitution':
        return `${event.playerIn?.name || 'Bilinmeyen Oyuncu'} ↔️ ${event.playerOut?.name || 'Bilinmeyen Oyuncu'}`;
      default:
        return event.text || 'Bilinmeyen Olay';
    }
  };

  if (loading) return <div className="text-center">Yükleniyor...</div>;

  return (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold mb-4">Maç Olayları</h3>
      <div className="flex flex-col space-y-2">
        {events.map((event) => (
          <div 
            key={event.id} 
            className={`flex items-center gap-2 p-2 rounded-lg ${
              event.isHome ? 'justify-start' : 'justify-end'
            } ${
              event.incidentType === 'goal' ? 'bg-green-50' :
              event.incidentType.includes('card') ? 'bg-red-50' :
              'bg-gray-50'
            }`}
          >
            {event.isHome ? (
              <>
                <span className="text-lg">{getEventIcon(event.incidentType)}</span>
                <div>
                  <span className="font-medium">{getEventText(event)}</span>
                  <span className="text-sm text-gray-500 ml-2">{event.time}'</span>
                </div>
              </>
            ) : (
              <>
                <div>
                  <span className="font-medium">{getEventText(event)}</span>
                  <span className="text-sm text-gray-500 mr-2">{event.time}'</span>
                </div>
                <span className="text-lg">{getEventIcon(event.incidentType)}</span>
              </>
            )}
          </div>
        ))}

        {events.length === 0 && (
          <p className="text-center text-gray-500">
            Henüz önemli bir maç olayı bulunmuyor.
          </p>
        )}
      </div>
    </div>
  );
};

export default MatchEvents;
