import React, { useEffect, useState } from 'react';
import { api } from '../services/api';

interface Match {
  id: number;
  homeTeam: { name: string; };
  awayTeam: { name: string; };
  score: {
    fullTime: {
      home: number | null;
      away: number | null;
    };
  };
  minute: number;
  status: string;
}

const LiveMatches = () => {
  const [matches, setMatches] = useState<Match[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchLiveMatches = async () => {
      setLoading(true);
      try {
        const response = await api.get('/matches?status=LIVE');
        setMatches(response.data.matches);
      } catch (error) {
        console.error('Canlı maçlar yüklenirken hata:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchLiveMatches();
    const interval = setInterval(fetchLiveMatches, 60000); // Her 1 dakikada bir güncelle

    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return <div className="flex justify-center p-8">Yükleniyor...</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Canlı Maçlar</h1>
      
      <div className="grid gap-4">
        {matches.map((match) => (
          <div 
            key={match.id} 
            className="bg-white rounded-lg shadow p-4 hover:shadow-lg transition"
          >
            <div className="flex justify-between items-center">
              <div className="flex-1">
                <p className="font-semibold">{match.homeTeam.name}</p>
              </div>
              
              <div className="flex items-center gap-2 px-4">
                <span className="text-xl font-bold">
                  {match.score.fullTime.home ?? 0}
                </span>
                <span className="text-sm text-red-600">
                  {match.minute}'
                </span>
                <span className="text-xl font-bold">
                  {match.score.fullTime.away ?? 0}
                </span>
              </div>
              
              <div className="flex-1 text-right">
                <p className="font-semibold">{match.awayTeam.name}</p>
              </div>
            </div>
          </div>
        ))}
        
        {matches.length === 0 && (
          <p className="text-center text-gray-500">
            Şu anda canlı maç bulunmuyor.
          </p>
        )}
      </div>
    </div>
  );
};

export default LiveMatches;
